class date
{
    int day, month, year;

public:
    date()
    {
        day = 0;
        month = 0;
        year = 0;
    }
    date(const int d, int m, int y)
    {
        day = d;
        month = m;
        year = y;
    }
    ~date()
    {
        cout << "DOB destructor called!" << endl;
    }
    void display()
    {
        cout << "Date is: " << day << '/' << month << '/' << year << endl;
    }
};

//
//  #####  ###    ###  #####
//  ##     ## #  # ##  ##  ##
//  #####  ##  ##  ##  #####
//  ##     ##      ##  ##
//  #####  ##      ##  ##
//


class emp
{
    // We can use protected to give acccess from parent class to child class

protected:
    char *name;
    char *des;
    date DOJ;

public:
    void print()
    {
        cout << "Emp name: " << name;
        cout << "Emp Designation: " << des;
        cout << "Date of joining: ";
        DOJ.display();
    }
    emp()
    {
        name = des = nullptr;
    }
    emp(char *n, char *d, date dat) : DOJ(dat) // Member initialser list
    {
        name = new char[strlen(n) + 1];
        des = new char[strlen(d) + 1];
        strcpy(name, n);
        strcpy(des, d);
    }
};
// SALARY
class salaried_emp : public emp
{
    int salary;

public:
    void print();
    salaried_emp(char *n, char *d, date dat, int s)
    {
        salary = s;
        emp(n, d, dat);
    }
};
void salaried_emp::print() // in main the print function to be called is determined by the collar object
{
    emp::print();
    cout << "Salary is: " << salary;
}
// HOURLY
class hourly_emp : public emp
{
    int hr;
    int rate;
};
// COMMISSIONED
class commissioned_emp : public emp
{
    int sales;
    float comm_rate;
};
int main()
{
}

